import { useState} from "react";

function TaskCreate({onCreate,task,taskformUpdate}) {

const [title,setTitle] = useState('');
const [ taskDesc,setTaskDesc] = useState('');
console.log(title,taskDesc);


    const handleChange = (event) =>{
      setTitle(event.target.value)
    }
    const handleTaskChange = (event) =>{
        setTaskDesc(event.target.value)
      }
      const handleSubmit = (event) =>{
        event.preventDefault();
        onCreate(title,taskDesc);
        setTitle("");
        setTaskDesc("");


      }

    return (
      <div>
        {''}
       {taskformUpdate ? (
      <div className="task-update">
      <h3>Lütfen Task Ekleyiniz!</h3>
      <form className = "task-form"> 
          <label className="task-label">Başlık</label>
           <input value={title} onChange ={handleChange}  className="task-input"/>
          <label className="task-label">Task Giriniz!</label>
          <textarea  value = {taskDesc} 
          onChange ={handleTaskChange} 
          className="task-input"
           rows={5}/>
          <button className="task-button-update" onClick={handleSubmit}>Oluştur</button>
      </form>
       </div>
    ):(
  <div className="task-create">
        <h3>Lütfen Task Ekleyiniz!</h3>
        <form className = "task-form"> 
            <label className="task-label">Başlık</label>
             <input value={title} onChange ={handleChange}  className="task-input"/>
            <label className="task-label">Task Giriniz!</label>
            <textarea  value = {taskDesc} 
            onChange ={handleTaskChange} 
            className="task-input"
             rows={5}/>
            <button className="task-button" onClick={handleSubmit}>Oluştur</button>
        </form>
    </div>
    )}
 </div>
    );
}
       

export default TaskCreate;
